            public static NautilusPrefab ParseConfig(string configText)
            {
                var config = new NautilusPrefab();
                var lines = configText.Split(new[] { '\n', '\r' }, StringSplitOptions.RemoveEmptyEntries);

                foreach (var line in lines)
                {
                    var trimmedLine = line.Trim();

                    if (trimmedLine.StartsWith("//"))
                        continue;

                    var parts = trimmedLine.Split('=');
                    if (parts.Length != 2)
                        continue;

                    var key = parts[0].Trim();
                    var value = parts[1].Trim();

                    switch (key)
                    {
                        case "PrefabVersion": config.PrefabVersion = int.Parse(value); break;
                        case "Name": config.Name = value; break;
                        case "Height": config.Height = float.Parse(value); break;
                        case "Weight": config.Weight = float.Parse(value); break;
                        case "Muscle": config.Muscle = float.Parse(value); break;
                        case "SkinColor": config.SkinColor = int.Parse(value); break;
                        case "EyeColor": config.EyeColor = int.Parse(value); break;
                        case "Cheek": config.Cheek = int.Parse(value); break;
                        case "CheekW": config.CheekW = float.Parse(value); break;
                        case "CheekV": config.CheekV = float.Parse(value); break;
                        case "Chin": config.Chin = int.Parse(value); break;
                        case "ChinW": config.ChinW = float.Parse(value); break;
                        case "ChinV": config.ChinV = float.Parse(value); break;
                        case "Nose": config.Nose = int.Parse(value); break;
                        case "NoseW": config.NoseW = float.Parse(value); break;
                        case "NoseV": config.NoseV = float.Parse(value); break;
                        case "Eye": config.Eye = int.Parse(value); break;
                        case "EyeA": config.EyeA = float.Parse(value); break;
                        case "EyeD": config.EyeD = float.Parse(value); break;
                        case "EyeV": config.EyeV = float.Parse(value); break;
                        case "Mouth": config.Mouth = int.Parse(value); break;
                        case "MouthW": config.MouthW = float.Parse(value); break;
                        case "MouthV": config.MouthV = float.Parse(value); break;
                        case "BrowD": config.BrowD = float.Parse(value); break;
                        case "BrowV": config.BrowV = float.Parse(value); break;
                        case "Guitar": config.Guitar = int.Parse(value); break;
                        case "Bass": config.Bass = int.Parse(value); break;
                        case "Drums": config.Drums = int.Parse(value); break;
                        case "Mic": config.Mic = int.Parse(value); break;
                        case "Keys": config.Keys = int.Parse(value); break;
                        case "Leg": config.Leg = int.Parse(value); break;
                        case "Torso": config.Torso = int.Parse(value); break;
                        case "Hands": config.Hands = int.Parse(value); break;
                        case "Feet": config.Feet = int.Parse(value); break;
                        case "FaceHair": config.FaceHair = int.Parse(value); break;
                        case "Glasses": config.Glasses = int.Parse(value); break;
                        case "Hair": config.Hair = int.Parse(value); break;
                        case "Wrist": config.Wrist = int.Parse(value); break;
                        default: break;
                    }
                }

                return config;
            }
        }

