        /// <summary>
        /// Converts a Nautilus .prefab file into a BandCharDesc.
        /// </summary>
        /// <param name="nautilusPrefab">The Nautilus .prefab to convert.</param>
        /// <returns>The Nautilus .prefab as a BandCharDesc.</returns>
        public static BandCharDesc ToBandCharDesc(string nautilusPrefab)
        {
            NautilusPrefab prefab = NautilusPrefab.ParseConfig(nautilusPrefab);
            BandCharDesc bandCharDesc = new BandCharDesc();
            bandCharDesc.skinColor = prefab.SkinColor;
            bandCharDesc.head = new Head();

            bandCharDesc.head.eye = prefab.Eye;
            bandCharDesc.head.eyeColor = prefab.EyeColor;
            bandCharDesc.head.eyeRotation = prefab.EyeA;
            bandCharDesc.head.eyeSeparation = prefab.EyeD;
            bandCharDesc.head.eyeHeight = prefab.EyeV;

            bandCharDesc.head.nose = prefab.Nose;
            bandCharDesc.head.noseWidth = prefab.NoseW;
            bandCharDesc.head.noseHeight = prefab.NoseV;

            bandCharDesc.head.mouth = prefab.Mouth;
            bandCharDesc.head.mouthWidth = prefab.MouthW;
            bandCharDesc.head.mouthHeight = prefab.MouthV;

            bandCharDesc.head.chin = prefab.Chin;
            bandCharDesc.head.chinWidth = prefab.ChinW;
            bandCharDesc.head.chinHeight = prefab.ChinV;

            bandCharDesc.head.jawWidth = prefab.CheekW;
            bandCharDesc.head.jawHeight = prefab.CheekV;

            bandCharDesc.head.mouth = prefab.Mouth;
            bandCharDesc.head.mouthWidth = prefab.MouthW;
            bandCharDesc.head.mouthHeight = prefab.MouthV;

            bandCharDesc.weight = prefab.Weight;
            bandCharDesc.height = prefab.Height;
            bandCharDesc.muscle = prefab.Muscle;

            bandCharDesc.outfit = new Outfit();
            bandCharDesc.outfit.hair = new OutfitPiece();
            bandCharDesc.outfit.hair.name = hair[prefab.Hair];
            bandCharDesc.outfit.glasses = new OutfitPiece();
            bandCharDesc.outfit.glasses.name = glasses[prefab.Glasses];
            bandCharDesc.outfit.faceHair = new OutfitPiece();
            bandCharDesc.outfit.faceHair.name = faceHair[prefab.FaceHair];
            bandCharDesc.outfit.wrist = new OutfitPiece();
            bandCharDesc.outfit.wrist.name = wrist[prefab.Wrist];
            bandCharDesc.outfit.torso = new OutfitPiece();
            bandCharDesc.outfit.torso.name = torso[prefab.Torso];
            bandCharDesc.outfit.legs = new OutfitPiece();
            bandCharDesc.outfit.legs.name = legs[prefab.Leg];
            bandCharDesc.outfit.feet = new OutfitPiece();
            bandCharDesc.outfit.feet.name = feet[prefab.Feet];
            bandCharDesc.outfit.hands = new OutfitPiece();
            bandCharDesc.outfit.hands.name = hands[prefab.Hands];

            bandCharDesc.instruments = new InstrumentOutfit();
            bandCharDesc.instruments.guitar.name = guitar[prefab.Guitar];
            bandCharDesc.instruments.bass.name = bass[prefab.Bass];
            bandCharDesc.instruments.drum.name = drum[prefab.Drums];
            bandCharDesc.instruments.mic.name = mic[prefab.Mic];
            bandCharDesc.instruments.keyboard.name = keys[prefab.Keys];

            bandCharDesc.revision = 17;

            bandCharDesc.objFields = new Assets.ObjectFields();
            bandCharDesc.objFields.revision = 2;

            // this breaks if the user did put male hair on a female and vice versa
            bandCharDesc.gender = IsMaleOrFemale(hair[prefab.Hair]);

            return bandCharDesc;
        }

        public static Symbol IsMaleOrFemale(string pieceName)
        {
            // if it starts with female, return female, otherwise male
            if (pieceName.StartsWith("male", StringComparison.OrdinalIgnoreCase))
                return new Symbol(4, "male");
            else if (pieceName.StartsWith("female", StringComparison.OrdinalIgnoreCase))
                return new Symbol(6, "female");
            else
                return new Symbol(4, "male");
        }
